import React from "react";
import PropTypes from 'prop-types'
import { browserHistory } from "react-router";
import {Link} from "react-router";

import { User } from "./User";

const API = 'https://hn.algolia.com/api/v1/search?query=';

export class Home extends React.Component {
    
    //ES6 - Check out
    constructor(props) {
        super();
        //React Property that sets our state
        this.state = {
            //You shouldn't assign props to your state, except for the case where this is the initial value
            hits: [],
            isLoading: false
        };

    }

    componentDidMount() {
        this.setState({ isLoading: true });

        fetch(API)
        .then(response => response.json())
        .then(data => this.setState({ hits: data.hits, isLoading: false }));
        console.log("Component did mount!");
    }
    onNavigateDetails(){
        browserHistory.push("/Details");
    } 

    render() {
        const { hits, isLoading } = this.state;

        if (isLoading) {
            return <div className="josephmain">
                <User/>
                <div className="config">
                    <div className="gridContainer">
                        <div className="grid-1" >
                        <img src={require('../Images/loading.gif')}/>                      
                            </div>
                        </div>
                    </div>
                </div>;
          }

        return (
            <div className="josephmain">
                <div>
                   
                </div>
                <User/>

                <div className="config">
                    <div className="gridContainer">
                        <div className="grid-1" >
                        
                        <ul >
                            {hits.map(hit =>
                                <li className ="list" key={hit.objectID}>    
                                <Link to={{
                                    pathname:"/Details",
                                    state:{
                                        artworkURL: hit.objectID,
                                        trackName: hit.title,
                                        artistName: hit.points,
                                        trackPrice: hit.story_text,
                                        durationTime: hit.num_comments,
                                        releaseDate: hit.story_title,
                                    }
                                    }} 
                                    activeClassName="active" className="detailslink" >Select Track</Link>                                    
                                    <p id="tracks">TrackName: {hit.title}</p>
                                    <p id="tracks">Artist: {hit.points}</p>
                                    <p id="tracks">Price: {hit.author}</p>
                                    <p id="tracks">Artwork URL: {hit.url}</p>
                                    <div className="linebreak"></div>
                                </li>
                            )}
                        </ul>                        
                            </div>
                        </div>

                    </div>
                </div>
               
           
        );
    }
}
Home.propTypes = {

};


