import React from "react";
import PropTypes from 'prop-types'

export class Home extends React.Component{
    //ES6 - Check out
    constructor(props){
        super();
        //React Property that sets our state
        this.state = {
            //You shouldn't assign props to your state, except for the case where this is the initial value
            age: props.initialAge,
            status:0,
            homeLink: props.initialLinkName
        };
        setTimeout(() =>{
            this.setState({
                status:1
            })
        },3000);
    console.log("Constructor");
    }
    //First method in the lifecycle hook that will get executed
    componentWillMount(){
        console.log("Component will mount");
    }
    componentDidMount()
    {
        console.log("Component did mount!");
    }
    componentWillReceiveProps(nextProps){
        console.log("Componnent will receive props", nextProps);
    }
    shouldComponentUpdate(nextProps, nextState){
        console.log("Should Component update", nextProps, nextState);
        //if(nextState.status ===1){
        //    return false;
        //}
        return true;
    }
    componentWillUpdate(nextProps, nextState){
        console.log("Component will update", nextProps, nextState);
    }
    componentDidUpdate(prevProps, prevState){
        console.log("Component did update", prevProps, prevState);
    }
    componentWillUnmount(){
        console.log("Component will unmount");
    }

    onMakeOlder(){
        //Very important method. This method allows you to change the state. Whenever you call this method react JS will trigger a re-rendering of the UI.
        //It takes a JS Object
        this.setState({
            age: this.state.age + 3
        });
    }    

    onChangeLink(){
        this.props.changeLink(this.state.homeLink);
    }

    onHandleChange(event){
        this.setState({
            homeLink: event.target.value
        });
    }

    render(){
        return(           
                <div className ="main">
                    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Quisque venenatis dolor nisi, vitae convallis leo varius in. Cras in odio eros. Vivamus tristique, mi at lacinia sollicitudin, nunc urna maximus turpis, id eleifend tortor odio id lectus. Class aptent taciti sociosqu ad litora torquent per conubia nostra, per inceptos himenaeos. Morbi consectetur varius nisl. Quisque id enim sed nibh imperdiet vehicula vel et dolor. Vivamus tempus eros at eros euismod, sed pretium dui laoreet. Praesent tempor volutpat aliquam. Donec et tortor tellus. Duis at odio sed purus convallis placerat.</p>
                    <hr/>
                    <p>Your name is {this.props.name}, your age is {this.state.age}</p>
                    <p>Status: {this.state.status}</p>
                    <hr/>
                    <button onClick={() => this.onMakeOlder()} className ="btn btn-primary">Make me older!</button>
                    <hr/>
                    <button onClick={this.props.greet} className ="btn btn-primary">Greet</button>
                    <hr/>
                    <input type ="text" value={this.state.homeLink} onChange={(event) => this.onHandleChange(event)}/>
                    <button onClick={this.onChangeLink.bind(this)} className ="btn btn-primary">Change Header Link</button>
                </div>           
        );
    }
}
Home.propTypes = {
    name: PropTypes.string,
    initialAge: PropTypes.number,
    greet: PropTypes.func,
    initialLinkName: PropTypes.string
};


//Button on click could also be this ~~~~~ <button onClick={this.onMakeOlder.bind(this)} className ="btn btn-primary">Make me older!</button>
